import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  // Función para obtener los partidos desde el backend (Flask)
  const fetchFootballMatches = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/football-matches');
      console.log(response.data); // Verifica la respuesta en la consola
      setMatches(response.data.response); // Almacena los partidos en el estado
      setLoading(false);  // Cambia el estado de carga a false
    } catch (error) {
      console.error('Error al obtener los partidos:', error);
      setLoading(false);
    }
  };

  // Llamar a la función para obtener los partidos al cargar el componente
  useEffect(() => {
    fetchFootballMatches();
  }, []);

  return (
    <div className="App">
      <h1>Partidos de Fútbol</h1>

      {loading ? (
        <p>Cargando...</p>
      ) : (
        <div>
          {matches && matches.length > 0 ? (
            <ul>
              {matches.map((match, index) => (
                <li key={index}>
                  <div>
                    <h3>{match.league.name} - {match.fixture.date}</h3>
                    <p>Estadio: {match.fixture.venue.name}, {match.fixture.venue.city}</p>
                    <p>
                      {match.fixture.status.long} ({match.fixture.status.short})
                    </p>
                    <p>
                      {match.fixture.status.long === "Match Finished" ? (
                        <>
                          <strong>{match.goals.home} - {match.goals.away}</strong>
                        </>
                      ) : (
                        <span>En progreso...</span>
                      )}
                    </p>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No hay partidos disponibles.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
