import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  // Función para obtener los partidos desde el backend Flask
  const fetchFootballMatches = async () => {
    try {
      // Hacemos la solicitud a nuestro backend Flask
      const response = await axios.get('http://localhost:5000/api/football-matches');
      console.log('Datos de la API:', response.data);  // Verificar en la consola
      setMatches(response.data.response);  // Guardar los partidos en el estado
      setLoading(false);  // Cambiar el estado a "no cargando"
    } catch (error) {
      console.error('Error al obtener los partidos:', error);
      setLoading(false);
    }
  };

  // Llamar a la función para obtener los partidos cuando se cargue el componente
  useEffect(() => {
    fetchFootballMatches();
  }, []);

  return (
    <div className="App">
      <h1>Partidos de Fútbol</h1>

      {loading ? (
        <p>Cargando...</p>
      ) : (
        <div className="matches-list">
          {matches && matches.length > 0 ? (
            <ul>
              {matches.map((match, index) => (
                <li key={index} className="match-item">
                  <div className="match-details">
                    <h3>{match.league.name} - {new Date(match.fixture.date).toLocaleString()}</h3>
                    <p>Estadio: {match.fixture.venue.name}, {match.fixture.venue.city}</p>
                    <p><strong>{match.fixture.status.long}</strong> ({match.fixture.status.short})</p>
                    <p>
                      {match.fixture.status.long === "Match Finished" ? (
                        <span>Resultado:</span>,
                        <h2>{match.goals.home} - {match.goals.away}</h2>
                      ) : (
                        <span>En progreso...</span>
                      )}
                    </p>
                    <div className="teams">
                      <div className="home-team">
                        <img 
                          src={match.teams.home.logo} 
                          alt={`${match.teams.home.name} logo`} 
                          className="team-logo"
                        />
                        <span>{match.teams.home.name}</span>
                      </div>
                      <div className="away-team">
                        <img 
                          src={match.teams.away.logo} 
                          alt={`${match.teams.away.name} logo`} 
                          className="team-logo"
                        />
                        <span>{match.teams.away.name}</span>
                      </div>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No hay partidos disponibles.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
