import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  // Función para obtener los partidos desde el backend (Flask)
  const fetchFootballMatches = async () => {
    try {
      const response = await axios.get('http://localhost:3000');
      setMatches(response.data.matches);  // Almacena los partidos en el estado
      setLoading(false);  // Cambia el estado de carga a false
    } catch (error) {
      console.error('Error al obtener los partidos:', error);
      setLoading(false);
    }
  };

  // Llamar a la función para obtener los partidos al cargar el componente
  useEffect(() => {
    fetchFootballMatches();
  }, []);

  return (
    <div className="App">
      <h1>Partidos de Fútbol</h1>

      {loading ? (
        <p>Cargando...</p>
      ) : (
        <div>
          {matches && matches.length > 0 ? (
            <ul>
              {matches.map((match, index) => (
                <li key={index}>
                  {match.homeTeam.name} vs {match.awayTeam.name} - Fecha: {match.utcDate}
                </li>
              ))}
            </ul>
          ) : (
            <p>No hay partidos disponibles.</p>
          )}
        </div>
      )}

      {/* Widget de fútbol */}
      <div id="wg-api-football-games"
           data-host="https://v3.football.api-sports.io"
           data-key="7a99b51da2483e264939742f2399d4be"
           data-date=""
           data-league=""
           data-season=""
           data-theme=""
           data-refresh="15"
           data-show-toolbar="true"
           data-show-errors="false"
           data-show-logos="true"
           data-modal-game="true"
           data-modal-standings="true"
           data-modal-show-logos="true">
      </div>
      <script type="module" src="https://widgets.api-sports.io/2.0.3/widgets.js"></script>
    </div>
  );
}

export default App;
